curl -X POST "https://api.reviewpoint.org/api/v1/auth/login" \
  -H "Content-Type: application/json" \
  -d '{
    "email": "user@example.com",
    "password": "your_password"
  }'

import requests

response = requests.post(
    "https://api.reviewpoint.org/api/v1/auth/login",
    json={
        "email": "user@example.com",
        "password": "your_password"
    }
)

if response.status_code == 200:
    data = response.json()
    access_token = data["access_token"]
    print(f"Login successful! Token: {access_token[:20]}...")
else:
    print(f"Login failed: {response.json()}")

const response = await fetch('https://api.reviewpoint.org/api/v1/auth/login', {
  method: 'POST',
  headers: {
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    email: 'user@example.com',
    password: 'your_password'
  })
});

if (response.ok) {
  const data = await response.json();
  const accessToken = data.access_token;
  console.log(`Login successful! Token: ${accessToken.substring(0, 20)}...`);
} else {
  const error = await response.json();
  console.error('Login failed:', error);
}

import requests

headers = {
    "Authorization": "Bearer YOUR_JWT_TOKEN"
}

response = requests.get(
    "https://api.reviewpoint.org/api/v1/auth/me",
    headers=headers
)

if response.status_code == 200:
    user_data = response.json()
    print(f"Logged in as: {user_data['name']} ({user_data['email']})")
else:
    print(f"Failed to get profile: {response.json()}")

const response = await fetch('https://api.reviewpoint.org/api/v1/auth/me', {
  method: 'GET',
  headers: {
    'Authorization': 'Bearer YOUR_JWT_TOKEN'
  }
});

if (response.ok) {
  const userData = await response.json();
  console.log(`Logged in as: ${userData.name} (${userData.email})`);
} else {
  const error = await response.json();
  console.error('Failed to get profile:', error);
}

const response = await fetch('https://api.reviewpoint.org/api/v1/health');

if (response.ok) {
  const healthData = await response.json();
  console.log(`API Status: ${healthData.status}`);
  console.log(`Version: ${healthData.version}`);
} else {
  console.error(`Health check failed: ${response.status}`);
}

fetch('https://api.reviewpoint.org/api/v1/metrics')
