import requests

headers = {
    "Authorization": "Bearer YOUR_JWT_TOKEN",
    "X-API-Key": "YOUR_API_KEY"
}

response = requests.get(
    "https://api.reviewpoint.org/api/v1/users",
    headers=headers,
    params={"limit": 10, "offset": 0}
)

if response.status_code == 200:
    users_data = response.json()
    print(f"Found {users_data['total']} users")
    for user in users_data['users']:
        print(f"- {user['name']} ({user['email']})")
else:
    print(f"Failed to fetch users: {response.json()}")

const response = await fetch('https://api.reviewpoint.org/api/v1/users?limit=10&offset=0', {
  method: 'GET',
  headers: {
    'Authorization': 'Bearer YOUR_JWT_TOKEN',
    'X-API-Key': 'YOUR_API_KEY'
  }
});

if (response.ok) {
  const usersData = await response.json();
  console.log(`Found ${usersData.total} users`);
  usersData.users.forEach(user => {
    console.log(`- ${user.name} (${user.email})`);
  });
} else {
  const error = await response.json();
  console.error('Failed to fetch users:', error);
}

fetch('https://api.reviewpoint.org/api/v1/uploads/export', {
  headers: { 'Authorization': 'Bearer <token>' }
})
  .then(res => res.text())
  .then(console.log);

package main
import (
  "net/http"
)
func main() {
  req, _ := http.NewRequest("GET", "https://api.reviewpoint.org/api/v1/uploads/export", nil)
  req.Header.Set("Authorization", "Bearer <token>")
  http.DefaultClient.Do(req)
}

OkHttpClient client = new OkHttpClient();
Request request = new Request.Builder()
  .url("https://api.reviewpoint.org/api/v1/uploads/export")
  .get()
  .addHeader("Authorization", "Bearer <token>")
  .build();
Response response = client.newCall(request).execute();

$ch = curl_init('https://api.reviewpoint.org/api/v1/uploads/export');
curl_setopt($ch, CURLOPT_HTTPHEADER, ['Authorization: Bearer <token>']);
$response = curl_exec($ch);
curl_close($ch);

import requests

headers = {
    "Authorization": "Bearer YOUR_JWT_TOKEN"
}

files = {
    "file": ("document.pdf", open("document.pdf", "rb"), "application/pdf")
}

data = {
    "description": "Research paper draft"
}

response = requests.post(
    "https://api.reviewpoint.org/api/v1/uploads",
    headers=headers,
    files=files,
    data=data
)

if response.status_code == 201:
    file_data = response.json()
    print(f"Upload successful! File ID: {file_data['id']}")
else:
    print(f"Upload failed: {response.json()}")

